// src/App.js
import React, { Component } from "react";

class TotalTimer extends Component {
  render() {
    return (
      <div className="">
        <h1>Stopwatch</h1>
        <Stopwatch />
      </div>
    );
  }
}

let ms = 0;
let s = 0;
let m = 0;

let myVar = () => setInterval(myTimer, 1000);

function myTimer() {
  m++;
  // console.log(m++);
}

class Stopwatch extends Component {
  state = {
    isRunning: false
  };

  handleClick = () => {
    myVar();
    this.setState({
      runningTime: m
    });

    //startTimer();
    // startTimer();
    // this.setState({
    //   runningTime: startTimer(),
    //   isRunning: true
    // });
  };

  getTimer = () => {
    return `${m}:${s < 10 ? "0" + s : s}:${ms < 10 ? "0" + ms : ms}`;
  };

  handleReset = () => {
    this.setState({ runningTime: 0 });
  };

  render() {
    const { runningTime } = this.state;
    return (
      <div>
        <p>{runningTime}ms</p>
        <button onClick={this.handleClick}>Start/Stop</button>
        <button onClick={this.handleReset}>Reset</button>
      </div>
    );
  }
}
export default TotalTimer;
