import React, { Component } from "react";
import Controls from "./Controls";

let s = 0;

const formattedSeconds = sec =>
  ("0" + Math.floor(sec / 60)).slice(-2) + ":" + ("0" + (sec % 60)).slice(-2);

class LocalTimer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      time: 0,
      gtime: ""
    };
  }

  dataSubmit = () => {
    this.props.onUpdate(
      {
        user: this.props.user,
        carType: this.props.car,
        taskNum: this.props.taskNum,
        localTime: this.state.time,
        globalTime: formattedSeconds(this.state.gtime)
      },
      this.props.index
    );
  };

  start = () => {
    console.log("Start Clicked");
    if (this.state.isRunning === false) {
      this.setState({ time: this.state.time, isRunning: true });
      this.timer = setInterval(() => {
      this.setState({ time: (s = s + 0.1).toFixed(1) });
      }, 100);
    }
  };

restart = () => {
  let rs = this.state.time
  this.setState({ time: this.state.time, isRunning: true });
    this.timer = setInterval(() => {
    this.setState({ time: rs++ })
  }, 100);
}  

  stop = () => {
    if (this.state.isRunning === true) {
      clearInterval(this.timer);
      this.setState({ isRunning: false });
      console.log(this.state.time);
    }
  };

  save = () => {
    this.setState({ gtime: this.props.time });
  };

  reset = () => {
    this.setState({ time: 0.0, isRunning: false, gtime: "" });
    s = 0.1;
  };
  render() {
    return (
      <div
        className={
          !this.state.isRunning && this.state.time !== 0
            ? "LocalTimerDone"
            : "LocalTimer"
        }
      >
        <div className="localHeader">
          <h3>Task {this.props.taskNum}</h3>

          <p
            className={
              !this.state.isRunning && this.state.time !== 0
                ? "localBodyDone"
                : ""
            }
          >
            Started at: {formattedSeconds(this.state.gtime)}
          </p>
        </div>
        <div className="localBody">
          <p
            className={
              !this.state.isRunning && this.state.time !== 0
                ? "localBodyDone"
                : ""
            }
          >
            {this.props.taskDescription}
          </p>

          {this.state.isRunning ? (
            <h4 className="LocalRunning">{this.state.time}</h4>
          ) : (
            <h4 className="LocalStop">{this.state.time}</h4>
          )}
          <button onClick={this.restart}>restart</button>  
        </div>

        <div className="LocalControl">
          <Controls
            isRunning={this.state.isRunning}
            start={() => {
              this.reset();
              this.start();
              this.save();
            }}
            stop={() => {
              this.stop();
              this.dataSubmit();
            }}
            reset={() => this.reset()}
          />
        </div>
      </div>
    );
  }
}

export default LocalTimer;
