import React, { Component } from "react";

class Controls extends Component {
  render() {
    const { isRunning, start, restart, stop, reset, time } = this.props;
    return (
      <div className="Control_Btns">
        {!isRunning && time === 0 ? (
          <button onClick={start} className="Controls_Start">
            Start
          </button>
        ) : null}
        {!isRunning && time !== 0 ? (
          <button onClick={restart} className="Controls_Start">
            Restart
          </button>
        ) : null}

        {isRunning ? (
          <button onClick={stop} className="Controls_Stop">
            Paused
          </button>
        ) : null}
        <button onClick={reset} className="Conrols_Reset">
          Reset
        </button>
      </div>
    );
  }
}

export default Controls;
