import React, { Component } from "react";
import "./App.css";
import GlobalTimeView from "./components/GlobalTimeView";
import Controls from "./components/Controls";
import SelectUser from "./components/SelectUser";
import LocalTimer from "./components/LocalTimer";
import Tabs from "./Tabs";

const tasks_car_a = [
  {
    idx: 1,
    taskNum: "1-1",
    taskDescription: "홈 위젯 메뉴 변경하기"
  },
  {
    idx: 2,
    taskNum: "1-2",
    taskDescription: "홈 위젯 메뉴의 위치 변경하기"
  },
  {
    idx: 3,
    taskNum: "2",
    taskDescription: "분할화면에 다른 것이 보이도록 바꿔주세요."
  },
  {
    idx: 4,
    taskNum: "3",
    taskDescription: "홈 위젯 메뉴 변경하기"
  },
  {
    idx: 5,
    taskNum: "4",
    taskDescription: "계기판 표시 정보"
  },
  {
    idx: 6,
    taskNum: "5",
    taskDescription: "홈 위젯 메뉴 변경하기"
  },
  {
    idx: 7,
    taskNum: "6",
    taskDescription: "홈 위젯 메뉴 변경하기"
  }
];

const tasks_car_b = [
  {
    idx: 1,
    taskNum: "7 Series 1-1",
    taskDescription: "홈 위젯 메뉴 변경하기"
  },
  {
    idx: 2,
    taskNum: "1-2",
    taskDescription: "홈 위젯 메뉴의 위치 변경하기"
  },
  {
    idx: 3,
    taskNum: "2",
    taskDescription: "분할화면에 다른 것이 보이도록 바꿔주세요."
  },
  {
    idx: 4,
    taskNum: "3",
    taskDescription: "홈 위젯 메뉴 변경하기"
  },
  {
    idx: 5,
    taskNum: "4",
    taskDescription: "계기판 표시 정보"
  },
  {
    idx: 6,
    taskNum: "5",
    taskDescription: "홈 위젯 메뉴 변경하기"
  },
  {
    idx: 7,
    taskNum: "6",
    taskDescription: "홈 위젯 메뉴 변경하기"
  }
];

const formattedSeconds = sec =>
  Math.floor(sec / 60) + ":" + ("0" + (sec % 60)).slice(-2);

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      secondsElapsed: 0,
      lastClearedIncrementer: null
    };
    this.incrementer = null;
  }

  handleStartClick() {
    this.incrementer = setInterval(
      () =>
        this.setState({
          secondsElapsed: this.state.secondsElapsed + 1
        }),
      1000
    );
  }

  handleStopClick() {
    clearInterval(this.incrementer);
    this.setState({
      lastClearedIncrementer: this.incrementer
    });
  }

  handleResetClick() {
    clearInterval(this.incrementer);
    this.setState({
      secondsElapsed: 0,
      laps: []
    });
  }

  handleLabClick() {
    this.setState({
      laps: this.state.laps.concat([this.state.secondsElapsed])
    });
  }
  // NEW

  render() {
    return (
      <div className="App">
        <header className="Header">
          <h1>AVN UT TIMER</h1>
          <SelectUser />
        </header>
        <Tabs>
          <div label="KIA K900">
            <div className="GlobalTime">
              {/* View */}
              <h1 className="stopwatch-timer">
                {formattedSeconds(this.state.secondsElapsed)}
              </h1>
              <div className="GlobalControl">
                {" "}
                {this.state.secondsElapsed === 0 ||
                this.incrementer === this.state.lastClearedIncrementer ? (
                  <Button
                    className="start-btn"
                    onClick={this.handleStartClick.bind(this)}
                  >
                    start
                  </Button>
                ) : (
                  <Button
                    className="stop-btn"
                    onClick={this.handleStopClick.bind(this)}
                  >
                    stop
                  </Button>
                )}
                {this.state.secondsElapsed !== 0 &&
                this.incrementer !== this.state.lastClearedIncrementer ? (
                  <Button onClick={this.handleLabClick.bind(this)}>lab</Button>
                ) : null}
                {this.state.secondsElapsed !== 0 &&
                this.incrementer === this.state.lastClearedIncrementer ? (
                  <Button onClick={this.handleResetClick.bind(this)}>
                    reset
                  </Button>
                ) : null}
              </div>
            </div>

            <div>
              {tasks_car_a.map(localtimer => (
                <LocalTimer
                  time={this.state.time}
                  taskNum={localtimer.taskNum}
                  taskDescription={localtimer.taskDescription}
                  key={localtimer.idx}
                />
              ))}
            </div>
          </div>
          <div label="BMW 7 Series">
            <div className="GlobalTime">
              <GlobalTimeView time={this.state.time} />
              <Controls
                isRunning={this.state.isRunning}
                start={() => this.start()}
                stop={() => this.stop()}
                reset={() => this.reset()}
              />
            </div>

            <div>
              {tasks_car_b.map(localtimer => (
                <LocalTimer
                  time={this.state.time}
                  taskNum={localtimer.taskNum}
                  taskDescription={localtimer.taskDescription}
                  key={localtimer.idx}
                />
              ))}
            </div>
          </div>
        </Tabs>
      </div>
    );
  }
}
const Button = props => (
  <button type="button" {...props} className={"btn " + props.className} />
);
export default App;
