import React, { Component } from "react";

class TotalTimer extends Component {
  run = () => {
    stopwatchEl.innerHTML = getTimer();
    ms++;
    if (ms == 100) {
      ms = 0;
      s++;
    }
    if (s == 60) {
      s = 0;
      m++;
    }
  };

  render() {
    return (
      <div>
        <h1>Total Timer</h1>
        <h2 className="Number">00:00:00</h2>
        <div className="ButtonArea">
          <button className="MainButton">Start</button>
        </div>
      </div>
    );
  }
}

export default TotalTimer;
