// src/App.js
import React, { Component } from "react";
import Controls from "./Controls";

class TotalTimer extends Component {
  render() {
    return (
      <div className="">
        <h1>Stopwatch</h1>
        <Stopwatch />
      </div>
    );
  }
}

let s = 0;

class Stopwatch extends Component {
  constructor(props) {
    super(props);
    this.state = {
      time: 0,
      isRunning: false
    };
  }

  handleClick = () => {
    if (this.state.isRunning === false) {
      this.setState({ time: this.state.time, isRunning: true });
      this.timer = setInterval(() => {
        this.setState({ time: s++ });
      }, 100);
    } else {
      clearInterval(this.timer);
      console.log(this.state.time);
      // 애를 밖으로 빼고 싶어
      this.setState({ isRunning: false });
    }
  };

  handleReset = () => {
    this.setState({ time: 0, isRunning: false });
    s = 0;
  };

  render() {
    const { isRunning } = this.state;
    return (
      <div>
        <p>TIME: {this.state.time}</p>
        <Controls isRunning={isRunning} start={() => this.handleClick} />

        <p>아래 버튼 지울거</p>
        <button onClick={this.handleClick}>
          {isRunning ? "Stop" : "Start"}
        </button>
        <button onClick={this.handleReset}>Reset</button>
      </div>
    );
  }
}
export default TotalTimer;
