import React, { Component } from "react";
import "./Controls.css";

class Controls extends Component {
  render() {
    const {
      isRunning,
      isSaved,
      start,
      restart,
      stop,
      reset,
      time,
      save
    } = this.props;

    var saveBtn;
    if (!isRunning && time === 0) {
      saveBtn = <button className="Controls_Save_Disabled">Save</button>;
    } else if (!isSaved && time !== 0) {
      saveBtn = (
        <button onClick={save} className="Controls_Save_Active">
          Save
        </button>
      );
    } else if (isSaved && time !== 0) {
      saveBtn = (
        <button onClick={save} className="Controls_Save_Saved">
          Saved!
        </button>
      );
    }

    return (
      <div className="Control_Btns">
        {/* <button onClick={reset} className="Conrols_Reset">
          Reset
        </button> */}
        {!isRunning && time === 0 ? (
          <button onClick={start} className="Controls_Start">
            Start
          </button>
        ) : null}
        {!isRunning && time !== 0 ? (
          <button onClick={restart} className="Controls_Start">
            Restart
          </button>
        ) : null}
        {isRunning ? (
          <button onClick={stop} className="Controls_Stop">
            Pause
          </button>
        ) : null}

        {saveBtn}
      </div>
    );
  }
}

export default Controls;
